      receivedParams: req.query,
      headers: req.headers
    });
  });

  // Route de debug pour voir les variables d'environnement
  app.get('/api/env-check', (req, res) => {
    console.log('V√©rification des variables d\'environnement');

    // Ne pas exposer les valeurs r√©elles en production, juste les noms des variables
    const envVars = Object.keys(process.env)
      .filter(key => key.startsWith('VITE_'))
      .reduce((obj, key) => {
        obj[key] = process.env[key] ? 'd√©fini' : 'non d√©fini';
